import React, { Component } from 'react';

import {
  Button,
  Form,
  FormGroup,
  Label,
  Input,
  Alert
} from 'reactstrap';

import {
  Redirect,
  Link
} from "react-router-dom";

import axios from 'axios';

import './App.css';


const emptyPost = {
  user: '1',
  product: '1',
  location: '',
  description: '',
  expiration_date: '',
  upload_date: '',
  time: ''
};

class NewPost extends Component {
  constructor(props) {
    super(props);
    this.state = {
      toMain: false,
      message: '',
      post: {...emptyPost},
    };

    this.handleInputChange = this.handleInputChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
   }

  handleInputChange(event) {
    const target = event.target;
    const name = target.name;
    const value = target.value;
    const newPost = {...this.state.post};

    newPost[name] = value;
    this.setState({post: newPost});
  }

  componentDidMount() {
    if (!this.props.match.params.id) {
      this.setState({post: {...emptyPost}});
    } else {
      axios.get('/api/' + this.props.match.url)
        .then(response => this.setState({post: {...response.data}}))
        .catch(error => this.handleError(error));
    }
  }

  handleSubmit(event) {
    const id = this.state.post.id || '';
    const product = this.state.product;
    const location = this.state.location;
    const description = this.state.description;
    const expiration_date = this.state.expiration_date;
    const upload_date = this.state.upload_date;
    const time = this.state.time;
    const method = this.state.book.id ? "PUT" : "POST";
    axios(`/api/posts/${id}`, {
      method: method,
      data: JSON.stringify(post),
      headers:{
        'Content-Type': 'application/json'
      }
    })
      .then(response => {
        let message = '';
        if (method === "POST") {
          message = 'post uploaded';
        }
        this.setState({toMain: true, message });
      })
      .catch(error => this.handleError(error));
    event.preventDefault();
  }

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <label>
          Location:
          <input type="text" value={this.state.value} onChange={this.handleChange} />
        </label>
        <input type="submit" value="Submit" />
      </form>
    );
  }


  handleError(error) {
    if (error.response) {
      // The request was made and the server responded with a status code
      // that falls out of the range of 2xx
      console.log(error.response.data);
      console.log(error.response.status);
      console.log(error.response.headers);
      this.setState({toMain: false, message: error.response.data});
    } else if (error.request) {
      // The request was made but no response was received
      // `error.request` is an instance of XMLHttpRequest
      console.log(error.request);
      this.setState({toMain: false, message: 'No response'});
    } else {
      // Something happened in setting up the request that
      // triggered an Error
      console.log('Error', error.message);
      this.setState({toMain: false, message: error.message});          
    }
  }
}

export default NewPost;
