import React, { Component } from 'react';
import './App.css';
import list from './Books.js';

// Search Component
class Search extends Component {

  render() {
    const { searchTerm, onSearchChange } = this.props;
      return (
        <div className="Search">
          <form>
            <input
              type="text"
              value={searchTerm}
              onChange={onSearchChange}
            />
          </form>
        </div>
      );
  }
}

// ItemList Component
class ItemList extends Component {

  searchItem(item) {
    return item.title.toLowerCase()
      .includes(this.props.searchTerm.toLowerCase());
  }

  render() {
    return (
      <div className="books">
      <ul>
      {this.props.list.filter(item => this.searchItem(item)).map(
        item =>
        <li key={item.id}>
          <span className="badge">{item.id}</span>
          <span className="title">
            <a href={item.url}>{item.title}</a>
          </span>
          <button
            className="delete"
            onClick={() => this.props.onDismiss(item.id)}>
              x
            </button>
        </li>
      )}
      </ul>
      </div>
    );
  }
}

class App extends Component {

  constructor(props) {
  super(props);

  this.state = {
    list: list,
    searchTerm: '',
  };

  this.onSearchChange = this.onSearchChange.bind(this);
  }

  onDismiss(id) {
    const updatedList = this.state.list.filter(item => item.id !== id);

    this.setState({list : updatedList});
  }

  render() {
    return (
      <div className="App">
        <form>
          <input 
            type="text" 
            value={this.state.searchTerm}
            onChange={this.onSearchChange}/>
        </form>
      {
        this.state.list.filter(item => this.searchItem(item)).map(
          item =>
          <div key={item.id}>
            <span>
              <a href={item.url}>{item.title}</a>:
            </span>
            &nbsp;
            <span>{item.author}</span>
            &nbsp;Year Published:
            <span>{item.year_published}</span>
            <span>
              <button onClick={() => this.onDismiss(item.id)}>
                Dismiss
              </button>
            </span>
          </div>
        )
      }
    </div>
  );
} 
}

export default App;
